mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM span_stat_application
    WHERE timestamp = #{fromDateTime}
)

mapper.insert:
INSERT INTO span_stat_application ( organization, applicationId, count, timestamp)
SELECT organization, applicationId,  SUM(count) , #{fromDateTime}
FROM  span_stat_agent
WHERE timestamp BETWEEN #{fromDateTime} AND #{toDateTime}
GROUP BY organization, applicationId
