mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM span_stat_application
    WHERE organization= #{organization} AND applicationId = #{applicationId} AND timestamp = #{from}
)

mapper.insert:
INSERT INTO span_stat_application (organization, applicationId, count, timestamp)
SELECT #{organization}, #{applicationId},  IFNULL(SUM(count),0) , #{from}
FROM  span_stat_agent
WHERE organization= #{organization} AND applicationId = #{applicationId} AND timestamp  >=  #{from} AND timestamp  <=  #{to}
