mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM span_stat_organization
    WHERE organization= #{organization} AND timestamp = #{from}
)

mapper.insert:
INSERT INTO span_stat_organization (organization, count, timestamp)
SELECT organization, SUM(count) , #{from}
FROM  span_stat_application
WHERE organization= #{organization} AND timestamp  >=  #{from} AND timestamp  <=  #{to}
