mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM paas_organization
    WHERE organization = #{organizationName}
)

mapper.update:
UPDATE paas_organization
SET database_name = #{databaseName}, hbase_namespace = #{hbaseNamespace}, enable = #{enable}, expire_time = #{expireTime}
WHERE organization = #{organization}

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM organization_key
    WHERE organization = #{organizationKey}
)

mapper.update:
UPDATE mgmt_database
SET database_status = #{databaseStatus}
WHERE database_name = #{databaseName}

UPDATE mgmt_hbase
