mapper.delete:
DELETE
FROM app_auth_user_group
WHERE application_id = #{applicationId} AND user_group_id = #{userGroupId}

mapper.update:
UPDATE app_auth_user_group
SET authority = #{authority} 
WHERE application_id = #{applicationId} AND user_group_id = #{userGroupId}

mapper.select:
SELECT configuration
FROM app_auth_config
WHERE application_id = #{applicationId}

SELECT IF(COUNT(*) = 0, 0, 1)
FROM manager
WHERE user_id = #{userId}
