mapper.delete:
DELETE
FROM app_auth_user_group
WHERE application_id = #{applicationId} AND user_group_id = #{userGroupId}

mapper.update:
UPDATE app_auth_user_group
SET role = #{role.name}, configuration = #{configurationString}
WHERE application_id = #{applicationId} AND user_group_id = #{userGroupId}

mapper.select:
SELECT application_id, user_group_id, role, configuration AS configurationString
FROM app_auth_user_group
WHERE application_id = #{applicationId}

SELECT EXISTS (
    SELECT 1
    FROM manager
    WHERE user_id = #{userId}
)
